import { CSSProperties, ReactNode, useState } from "react";
import {
  getDisplacementFilter,
  DisplacementOptions,
} from "./getDisplacementFilter";
import { getDisplacementMap } from "./getDisplacementMap";
import styles from "./GlassElement.module.css";

type GlassElementProps = DisplacementOptions & {
  children?: ReactNode | undefined;
  blur?: number;
  debug?: boolean;
  zoom?: number;
};

export const GlassElement = ({
  height,
  width,
  depth: baseDepth,
  radius,
  children,
  strength,
  chromaticAberration,
  blur = 2,
  debug = false,
  zoom = 1,
}: GlassElementProps) => {
  /* Change element depth on click */
  const [clicked, setClicked] = useState(false);
  let depth = baseDepth / (clicked ? 0.7 : 1);
  // Pour l'effet loupe : position du curseur
  const [mouse, setMouse] = useState<{ x: number; y: number }>({ x: width / 2, y: height / 2 });

  // Gestion du déplacement du contenu zoomé
  const handleMouseMove = (e: React.MouseEvent<HTMLDivElement>) => {
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    setMouse({ x, y });
  };
  /* Effet loupe : zoom sur le fond de la page */
  const style: CSSProperties = {
    height: `${height}px`,
    width: `${width}px`,
    borderRadius: `${radius}px`,
    overflow: "hidden",
    position: "absolute",
    left: 0,
    top: 0,
    pointerEvents: "none",
    backgroundImage: `url(${typeof window !== "undefined" ? window.location.href : ""})`, // à remplacer par une image du fond si possible
    backgroundRepeat: "no-repeat",
    backgroundSize: `${window.innerWidth * zoom}px ${window.innerHeight * zoom}px`,
    backgroundPosition: `-${(window.scrollX + (window.innerWidth / 2 - width / 2 + mouse.x - width / 2) ) * (zoom - 1)}px -${(window.scrollY + (window.innerHeight / 2 - height / 2 + mouse.y - height / 2)) * (zoom - 1)}px`,
    boxShadow: "0 4px 32px rgba(0,0,0,0.2)",
    zIndex: 9999,
    // Optionnel : effet glass
    backdropFilter: `blur(${blur}px) brightness(1.1) saturate(1.5)`,
  };

  /* Debug mode: display the displacement map instead of actual effect */
  if (debug === true) {
    style.backgroundImage = `url("${getDisplacementMap({
      height,
      width,
      radius,
      depth,
    })}")`;
    style.boxShadow = "none";
  }

  // La div principale affiche le fond zoomé
  return (
    <div
      className={styles.box}
      style={style}
      onMouseDown={() => setClicked(true)}
      onMouseUp={() => setClicked(false)}
      onMouseMove={zoom > 1 ? handleMouseMove : undefined}
    >
      {/* Optionnel : overlay pour le contenu ou effet glass */}
    </div>
  );
}
